from fastapi.testclient import TestClient
from app import app # Import your FastAPI app instance

client = TestClient(app)

def test_fibonacci_n_1():
    response = client.get("/fibonacci/1")
    assert response.status_code == 200
    assert response.json() == {"fibonacci": "1"}

def test_fibonacci_n_100():
    response = client.get("/fibonacci/100")
    assert response.status_code == 200
    assert response.json() == {"fibonacci": "354224848179261915075"}

